import React, {useState, useEffect, useRef} from 'react';
import API from '../api';
import { useNavigate } from 'react-router-dom';

export default function Checkout(){
  const [shipping, setShipping] = useState({name:'',address:'',city:'',postalCode:'',phone:''});
  const [paymentMethod, setPaymentMethod] = useState('COD');
  const [cart, setCart] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [paypalOrder, setPaypalOrder] = useState(null);
  const [showUpiQr, setShowUpiQr] = useState(false);
  const [showCardForm, setShowCardForm] = useState(false);
  const [cardDetails, setCardDetails] = useState({
    number: '',
    name: '',
    expiry: '',
    cvv: ''
  });
  const [upiId, setUpiId] = useState('');
  const paypalButtonsRef = useRef(null);
  const token = localStorage.getItem('token');
  const navigate = useNavigate();

  // Fetch cart data
  useEffect(() => {
    if(token){
      API.get('/cart', {headers:{Authorization:'Bearer '+token}})
        .then(r => setCart(r.data))
        .catch(e => console.error(e));
    }
  }, [token]);

  // Load PayPal script when needed
  useEffect(() => {
    if (paymentMethod === 'PAYPAL' && paypalOrder) {
      const script = document.createElement('script');
      // Simple PayPal integration with demo client ID
      script.src = `https://www.paypal.com/sdk/js?client-id=AZDxjDScFpQtjWTOUtWKbyN_bDt4OgqaF4eYXlewfBP4-8aqX3PiV8e1GWU6liB2CUXlkA59kJXE7M6R&currency=INR&intent=capture&disable-funding=credit,card`;
      script.async = true;
      script.onload = () => {
        console.log('PayPal script loaded successfully');
        initializePayPalButtons();
      };
      script.onerror = (error) => {
        console.error('Error loading PayPal script:', error);
      };

      document.body.appendChild(script);

      return () => {
        if (script.parentNode) {
          script.parentNode.removeChild(script);
        }
      };
    }
  }, [paymentMethod, paypalOrder]);

  const initializePayPalButtons = () => {
    if (window.paypal && paypalOrder) {
      // Clear any existing PayPal buttons
      if (paypalButtonsRef.current) {
        paypalButtonsRef.current.innerHTML = '';
      }

      try {
        console.log('Initializing PayPal buttons');
        window.paypal.Buttons({
          // Simple button configuration
          style: {
            color: 'gold',
            shape: 'rect',
            label: 'paypal',
            height: 40
          },
          // Simplified order creation
          createOrder: (data, actions) => {
            console.log('Creating PayPal order...');
            return actions.order.create({
              purchase_units: [{
                amount: {
                  currency_code: 'INR',
                  value: paypalOrder.order.totalAmount.toFixed(2)
                },
                description: 'Purchase from The Party Shop'
              }]
            });
        },
          onApprove: async (data, actions) => {
            setIsLoading(true);
            console.log('Payment approved, attempting to capture funds...');
            try {
              // Capture the funds from the transaction
              console.log('PayPal data:', data);
              const details = await actions.order.capture();
              console.log('Payment successful, details:', details);

              // Send payment details to our server with India-specific details
              await API.post('/orders/capture-paypal-payment', {
                orderId: paypalOrder.order._id,
                paymentId: details.id,
                payerEmail: details.payer.email_address || 'test@example.com',
                payerCountry: 'IN',
                currency: 'INR'
              }, { headers: { Authorization: 'Bearer '+token } });

            setIsLoading(false);
            alert('Payment successful!');
            navigate('/orders');
            } catch (error) {
              setIsLoading(false);
              console.error('PayPal payment error:', error);
              console.error('Error details:', JSON.stringify(error, null, 2));
              alert('Payment failed: ' + (error.message || 'Unknown error. Please try again.'));
            }
          },
          onError: (err) => {
            console.error('PayPal error:', err);
            console.error('Error details:', JSON.stringify(err, null, 2));
            alert('Payment system error: ' + (err.message || 'Please try again later.'));
          },
          onCancel: () => {
            console.log('Payment cancelled by user');
            alert('Payment was cancelled. You can try again when ready.');
          }
        }).render(paypalButtonsRef.current);
      } catch (error) {
        console.error('Error rendering PayPal buttons:', error);
        alert('Failed to initialize payment system. Please try again later.');
      }
    } else {
      console.error('PayPal not loaded or order not available');
      if (!window.paypal) console.error('PayPal SDK not loaded');
      if (!paypalOrder) console.error('PayPal order not available');
    }
  };

  const submit = async (e)=>{
    e.preventDefault();
    if(!token){ return navigate('/login'); }

    try{
      setIsLoading(true);
      if(paymentMethod === 'PAYPAL') {
        // Create order with PayPal
        const r = await API.post('/orders', {
          shipping, 
          paymentMethod: 'PAYPAL'
        }, { headers: { Authorization: 'Bearer '+token } });

        setIsLoading(false);
        setPaypalOrder(r.data);
        // PayPal buttons will be rendered by the useEffect
      } else {
        // Cash on Delivery flow
        const r = await API.post('/orders', {
          shipping, 
          paymentMethod: 'COD'
        }, { headers: { Authorization: 'Bearer '+token } });

        setIsLoading(false);
        alert('Order placed! Order id: ' + r.data._id + ' (Cash on Delivery)');
        navigate('/orders');
      }
    } catch(e){
      setIsLoading(false);
      alert(e.response?.data?.message || 'Order failed'); 
    }
  };

  // Calculate total amount
  const subtotal = cart?.items?.reduce((s,it)=>s + (it.product.price * it.qty), 0) || 0;

  return (
    <div className="max-w-md mx-auto bg-white rounded shadow p-6">
      <h2 className="text-xl font-semibold mb-4">Checkout</h2>

      {/* Order Summary */}
      <div className="mb-6 p-4 bg-gray-50 rounded">
        <h3 className="font-medium mb-2">Order Summary</h3>
        <div className="flex justify-between">
          <span>Subtotal:</span>
          <span>₹{subtotal.toFixed(0)}</span>
        </div>
        <div className="flex justify-between font-semibold mt-2">
          <span>Total:</span>
          <span>₹{subtotal.toFixed(0)}</span>
        </div>
      </div>

      {!paypalOrder ? (
        <form onSubmit={submit}>
          {/* Shipping Information */}
          <h3 className="font-medium mb-2">Shipping Information</h3>
          <input className="w-full border p-2 rounded mb-3" placeholder="Full name" required value={shipping.name} onChange={e=>setShipping({...shipping,name:e.target.value})} />
          <input className="w-full border p-2 rounded mb-3" placeholder="Address" required value={shipping.address} onChange={e=>setShipping({...shipping,address:e.target.value})} />
          <input className="w-full border p-2 rounded mb-3" placeholder="City" required value={shipping.city} onChange={e=>setShipping({...shipping,city:e.target.value})} />
          <input className="w-full border p-2 rounded mb-3" placeholder="Postal Code" required value={shipping.postalCode} onChange={e=>setShipping({...shipping,postalCode:e.target.value})} />
          <input className="w-full border p-2 rounded mb-3" placeholder="Phone" required value={shipping.phone} onChange={e=>setShipping({...shipping,phone:e.target.value})} />

          {/* Payment Method Selection */}
          <h3 className="font-medium mb-2">Payment Method</h3>
          <div className="flex flex-col gap-2 mb-4">
            <label className="flex items-center gap-2">
              <input 
                type="radio" 
                name="paymentMethod" 
                value="COD" 
                checked={paymentMethod === 'COD'} 
                onChange={() => setPaymentMethod('COD')} 
              />
              <span>Cash on Delivery</span>
            </label>
            <label className="flex items-center gap-2">
              <input 
                type="radio" 
                name="paymentMethod" 
                value="PAYPAL" 
                checked={paymentMethod === 'PAYPAL'} 
                onChange={() => setPaymentMethod('PAYPAL')} 
              />
              <span>Pay Online (PayPal)</span>
            </label>
          </div>

          <button 
            disabled={isLoading} 
            className={`${isLoading ? 'bg-gray-400' : 'bg-green-600'} text-white px-4 py-2 rounded w-full`}
          >
            {isLoading ? 'Processing...' : paymentMethod === 'PAYPAL' ? 'Continue to PayPal' : 'Place Order (COD)'}
          </button>
        </form>
      ) : (
        <div>
          <h3 className="font-medium mb-4">Complete your payment</h3>
          <p className="text-sm text-gray-600 mb-4">Pay securely using PayPal</p>

          {!showUpiQr && !showCardForm && (
            <>
              {/* PayPal/UPI button */}
              <button 
                onClick={() => setShowUpiQr(true)}
                className="bg-yellow-500 text-white w-full py-3 rounded font-medium flex items-center justify-center mb-4"
                disabled={isLoading}
              >
                Pay with PayPal/UPI
              </button>

              {/* Credit/Debit Card Payment Button */}
              <button 
                onClick={() => setShowCardForm(true)}
                className="bg-blue-600 text-white w-full py-3 rounded font-medium flex items-center justify-center"
                disabled={isLoading}
              >
                Pay with Credit/Debit Card
              </button>
            </>
          )}

          {/* UPI QR Code */}
          {showUpiQr && (
            <div className="border p-4 rounded mb-4">
              <h4 className="font-medium mb-2">Scan QR Code to Pay</h4>
              <div className="flex justify-center mb-3">
                <div className="bg-gray-100 p-4 rounded w-48 h-48 flex items-center justify-center">
                  {/* Demo QR code - would be a real QR in production */}
                  <div className="border-2 border-gray-800 w-36 h-36 grid grid-cols-5 grid-rows-5 p-1">
                    {Array(25).fill(0).map((_, i) => (
                      <div key={i} className={`${Math.random() > 0.7 ? 'bg-gray-800' : ''}`}></div>
                    ))}
                  </div>
                </div>
              </div>
              <div className="mb-3">
                <label className="block text-sm font-medium mb-1">Enter UPI ID</label>
                <input 
                  type="text" 
                  className="w-full border p-2 rounded" 
                  placeholder="username@upi"
                  value={upiId}
                  onChange={(e) => setUpiId(e.target.value)}
                />
              </div>
              <div className="flex space-x-2">
                <button
                  onClick={async () => {
                    if (!upiId.trim()) {
                      alert('Please enter UPI ID');
                      return;
                    }
                    setIsLoading(true);
                    try {
                      await API.post('/orders/capture-paypal-payment', {
                        orderId: paypalOrder.order._id,
                        paymentId: 'upi-payment-' + Date.now(),
                        payerEmail: upiId,
                        payerCountry: 'IN',
                        currency: 'INR'
                      }, { headers: { Authorization: 'Bearer '+token } });

                      setIsLoading(false);
                      alert('Payment successful!');
                      navigate('/orders');
                    } catch (error) {
                      setIsLoading(false);
                      console.error('Payment error:', error);
                      alert('Payment failed. Please try again.');
                    }
                  }}
                  className="bg-green-600 text-white px-4 py-2 rounded flex-1"
                  disabled={isLoading}
                >
                  {isLoading ? 'Processing...' : 'Pay Now'}
                </button>
                <button
                  onClick={() => setShowUpiQr(false)}
                  className="border border-gray-300 px-4 py-2 rounded"
                  disabled={isLoading}
                >
                  Cancel
                </button>
              </div>
            </div>
          )}

          {/* Credit Card Form */}
          {showCardForm && (
            <div className="border p-4 rounded mb-4">
              <h4 className="font-medium mb-2">Enter Card Details</h4>
              <div className="mb-3">
                <label className="block text-sm font-medium mb-1">Card Number</label>
                <input 
                  type="text" 
                  className="w-full border p-2 rounded" 
                  placeholder="1234 5678 9012 3456"
                  value={cardDetails.number}
                  onChange={(e) => setCardDetails({...cardDetails, number: e.target.value})}
                />
              </div>
              <div className="mb-3">
                <label className="block text-sm font-medium mb-1">Name on Card</label>
                <input 
                  type="text" 
                  className="w-full border p-2 rounded" 
                  placeholder="John Doe"
                  value={cardDetails.name}
                  onChange={(e) => setCardDetails({...cardDetails, name: e.target.value})}
                />
              </div>
              <div className="flex space-x-4 mb-3">
                <div className="flex-1">
                  <label className="block text-sm font-medium mb-1">Expiry Date</label>
                  <input 
                    type="text" 
                    className="w-full border p-2 rounded" 
                    placeholder="MM/YY"
                    value={cardDetails.expiry}
                    onChange={(e) => setCardDetails({...cardDetails, expiry: e.target.value})}
                  />
                </div>
                <div className="w-24">
                  <label className="block text-sm font-medium mb-1">CVV</label>
                  <input 
                    type="text" 
                    className="w-full border p-2 rounded" 
                    placeholder="123"
                    value={cardDetails.cvv}
                    onChange={(e) => setCardDetails({...cardDetails, cvv: e.target.value})}
                  />
                </div>
              </div>
              <div className="flex space-x-2">
                <button
                  onClick={async () => {
                    // Basic validation
                    if (!cardDetails.number || !cardDetails.name || !cardDetails.expiry || !cardDetails.cvv) {
                      alert('Please fill all card details');
                      return;
                    }
                    setIsLoading(true);
                    try {
                      await API.post('/orders/capture-paypal-payment', {
                        orderId: paypalOrder.order._id,
                        paymentId: 'card-payment-' + Date.now(),
                        payerEmail: 'card-payment@example.com',
                        payerCountry: 'IN',
                        currency: 'INR'
                      }, { headers: { Authorization: 'Bearer '+token } });

                      setIsLoading(false);
                      alert('Payment successful!');
                      navigate('/orders');
                    } catch (error) {
                      setIsLoading(false);
                      console.error('Payment error:', error);
                      alert('Payment failed. Please try again.');
                    }
                  }}
                  className="bg-blue-600 text-white px-4 py-2 rounded flex-1"
                  disabled={isLoading}
                >
                  {isLoading ? 'Processing...' : 'Pay Now'}
                </button>
                <button
                  onClick={() => setShowCardForm(false)}
                  className="border border-gray-300 px-4 py-2 rounded"
                  disabled={isLoading}
                >
                  Cancel
                </button>
              </div>
            </div>
          )}

          <button 
            onClick={() => setPaypalOrder(null)} 
            className="text-gray-600 underline mt-4 block"
          >
            Cancel and return to checkout
          </button>
        </div>
      )}
    </div>
  );
}
