import React, {useState} from 'react';
import API from '../api';
import { useNavigate } from 'react-router-dom';

export default function Checkout(){
  const [shipping, setShipping] = useState({name:'',address:'',city:'',postalCode:'',phone:''});
  const token = localStorage.getItem('token');
  const navigate = useNavigate();

  const submit = async (e)=>{
    e.preventDefault();
    if(!token){ return navigate('/login'); }
    try{
      const r = await API.post('/orders', {shipping}, { headers: { Authorization: 'Bearer '+token }});
      alert('Order placed! Order id: ' + r.data._id + ' (Cash on Delivery)');
      navigate('/orders');
    }catch(e){ alert(e.response?.data?.message || 'Order failed'); }
  };

  return (
    <div className="max-w-md mx-auto bg-white rounded shadow p-6">
      <h2 className="text-xl font-semibold mb-4">Checkout (Cash on Delivery)</h2>
      <form onSubmit={submit}>
        <input className="w-full border p-2 rounded mb-3" placeholder="Full name" value={shipping.name} onChange={e=>setShipping({...shipping,name:e.target.value})} />
        <input className="w-full border p-2 rounded mb-3" placeholder="Address" value={shipping.address} onChange={e=>setShipping({...shipping,address:e.target.value})} />
        <input className="w-full border p-2 rounded mb-3" placeholder="City" value={shipping.city} onChange={e=>setShipping({...shipping,city:e.target.value})} />
        <input className="w-full border p-2 rounded mb-3" placeholder="Postal Code" value={shipping.postalCode} onChange={e=>setShipping({...shipping,postalCode:e.target.value})} />
        <input className="w-full border p-2 rounded mb-3" placeholder="Phone" value={shipping.phone} onChange={e=>setShipping({...shipping,phone:e.target.value})} />
        <button className="bg-green-600 text-white px-4 py-2 rounded">Place Order (COD)</button>
      </form>
    </div>
  );
}
