import React, {useEffect, useState} from 'react';
import API from '../api';
import { Link } from 'react-router-dom';

export default function Home(){
  const [products, setProducts] = useState([]);
  useEffect(()=>{ API.get('/products').then(r=>setProducts(r.data)).catch(e=>console.error(e)); },[]);
  return (
    <div>
      <section className="bg-white rounded-lg p-6 mb-6 flex items-center gap-6">
        <div>
          <h1 className="text-3xl font-bold">Make every party special ðŸŽ‰</h1>
          <p className="text-gray-600">Decorations, tableware, hats, confetti and more â€” delivered fast.</p>
          <Link to="/" className="inline-block mt-4 bg-pink-500 text-white px-4 py-2 rounded">Shop now</Link>
        </div>
        <img src="/images/balloons.png" className="w-48 h-48 object-contain" alt=""/>
      </section>

      <h2 className="text-xl font-semibold mb-4">Featured</h2>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
        {products.map(p=>(
          <div key={p._id} className="bg-white rounded shadow p-4">
            <Link to={'/product/'+p._id}><img src={p.image} alt={p.title} className="h-48 w-full object-contain mb-3"/></Link>
            <div className="flex justify-between items-start">
              <div>
                <h3 className="font-medium">{p.title}</h3>
                <p className="text-sm text-gray-500">{p.category}</p>
              </div>
              <div className="text-right">
                <div className="text-lg font-semibold">â‚¹{p.price}</div>
                <Link to={'/product/'+p._id} className="mt-2 inline-block bg-pink-500 text-white px-3 py-1 rounded">View</Link>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
