import React, {useEffect, useState} from 'react';
import API from '../api';
import { useParams, useNavigate } from 'react-router-dom';

export default function ProductDetails(){
  const {id} = useParams();
  const [product, setProduct] = useState(null);
  const navigate = useNavigate();
  useEffect(()=>{ API.get('/products/'+id).then(r=>setProduct(r.data)).catch(e=>console.error(e)); },[id]);

  const addToCart = async () => {
    const token = localStorage.getItem('token');
    const user = JSON.parse(localStorage.getItem('user'));

    if (!token) {
      return navigate('/login');
    }

    // Check if user is admin
    if (user && user.isAdmin) {
      alert('Admin users cannot add products to cart.');
      return;
    }

    try {
      await API.post('/cart/add', {productId: id, qty: 1}, { 
        headers: { Authorization: 'Bearer ' + token } 
      });
      alert('Added to cart');
      navigate('/cart');
    } catch (error) {
      console.error('Error adding to cart:', error);
      alert('Failed to add item to cart. Please try again.');
    }
  };

  if(!product) return <div>Loading...</div>;
  return (
    <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
      <div className="md:col-span-1">
        <img src={product.image} alt="" className="w-full h-80 object-contain" />
      </div>
      <div className="md:col-span-2">
        <h2 className="text-2xl font-bold mb-2">{product.title}</h2>
        <p className="text-gray-600 mb-4">{product.description}</p>
        <div className="text-2xl font-semibold mb-4">â‚¹{product.price.toFixed(0)}</div>
        <button onClick={addToCart} className="bg-pink-500 text-white px-4 py-2 rounded">Add to cart</button>
      </div>
    </div>
  );
}
