import React, { useEffect, useState } from 'react';
import API from '../api';
import { useParams, useNavigate } from 'react-router-dom';
import { FaStar, FaStarHalfAlt, FaRegStar, FaUserCircle } from 'react-icons/fa';

// Star Rating Component
const StarRating = ({ rating, onRatingChange, interactive = false, size = 'md' }) => {
  const sizeClasses = {
    sm: 'w-4 h-4',
    md: 'w-5 h-5',
    lg: 'w-6 h-6'
  };

  return (
    <div className="flex">
      {[1, 2, 3, 4, 5].map((star) => {
        const starProps = {
          className: `${sizeClasses[size]} ${interactive ? 'cursor-pointer' : 'cursor-default'}`,
          onClick: () => interactive && onRatingChange(star),
          onMouseEnter: () => interactive && onRatingChange(star)
        };

        return (
          <button
            key={star}
            type="button"
            className="focus:outline-none"
          >
            {rating >= star ? (
              <FaStar {...starProps} className={`${starProps.className} text-yellow-400`} />
            ) : rating >= star - 0.5 ? (
              <FaStarHalfAlt {...starProps} className={`${starProps.className} text-yellow-400`} />
            ) : (
              <FaRegStar {...starProps} className={`${starProps.className} text-gray-300`} />
            )}
          </button>
        );
      })}
    </div>
  );
};

// Review Form Component
const ReviewForm = ({ onSubmit, onCancel }) => {
  const [rating, setRating] = useState(5);
  const [comment, setComment] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!comment.trim()) {
      setError('Please enter your review');
      return;
    }

    setIsSubmitting(true);
    setError('');

    try {
      await onSubmit({ rating, comment });
      setComment('');
      setRating(5);
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to submit review');
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="mt-6 p-4 bg-gray-50 rounded-lg">
      <h3 className="text-lg font-medium mb-3">Write a Review</h3>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label className="block text-sm font-medium text-gray-700 mb-1">Your Rating</label>
          <StarRating rating={rating} onRatingChange={setRating} interactive={true} />
        </div>
        <div className="mb-3">
          <label htmlFor="comment" className="block text-sm font-medium text-gray-700 mb-1">
            Your Review
          </label>
          <textarea
            id="comment"
            rows="3"
            className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:ring-pink-500 focus:border-pink-500"
            value={comment}
            onChange={(e) => setComment(e.target.value)}
            placeholder="Share your experience with this product..."
            required
          />
        </div>
        {error && <p className="text-red-500 text-sm mb-3">{error}</p>}
        <div className="flex space-x-2">
          <button
            type="submit"
            disabled={isSubmitting}
            className="px-4 py-2 bg-pink-600 text-white rounded-md hover:bg-pink-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-pink-500 disabled:opacity-50"
          >
            {isSubmitting ? 'Submitting...' : 'Submit Review'}
          </button>
          {onCancel && (
            <button
              type="button"
              onClick={onCancel}
              className="px-4 py-2 border border-gray-300 rounded-md text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-pink-500"
            >
              Cancel
            </button>
          )}
        </div>
      </form>
    </div>
  );
};

