export default function ProductDetails() {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [showReviewForm, setShowReviewForm] = useState(false);
  const [reviews, setReviews] = useState([]);
  const [averageRating, setAverageRating] = useState(0);
  const [reviewCount, setReviewCount] = useState(0);
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem('user'));
  const token = localStorage.getItem('token');

  const fetchProduct = async () => {
    try {
      setIsLoading(true);
      console.log('Fetching product with ID:', id);

      const productRes = await API.get(`/api/products/${id}`);
      console.log('Product response:', productRes);

      if (!productRes.data) {
        throw new Error('No product data received');
      }

      // Initialize with empty reviews if the endpoint doesn't exist yet
      let reviewsData = { reviews: [], averageRating: 0, count: 0 };
      try {
        console.log('Fetching reviews for product ID:', id);
        const reviewsRes = await API.get(`/api/products/${id}/reviews`);
        console.log('Reviews response:', reviewsRes);
        reviewsData = reviewsRes.data || { reviews: [], averageRating: 0, count: 0 };
      } catch (error) {
        console.warn('Could not fetch reviews:', error.message);
        // Continue with default reviews data
      }

      setProduct(productRes.data);
      setReviews(reviewsData.reviews || []);
      setAverageRating(reviewsData.averageRating || 0);
      setReviewCount(reviewsData.count || 0);
    } catch (error) {
      console.error('Error fetching product:', error);
      // Set product to an empty object to prevent "product not found" error
      setProduct({});
      // Show error message to user
      alert(`Error loading product: ${error.message || 'Please try again later.'}`);
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    fetchProduct();
  }, [id]);

  const handleReviewSubmit = async (reviewData) => {
    if (!token) {
      navigate('/login');
      return;
    }

    try {
      await API.post(
        `/api/products/${id}/reviews`,
        reviewData,
        {
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`
          }
        }
      );

      // Refresh reviews after submission
      await fetchProduct();
      setShowReviewForm(false);
    } catch (error) {
      console.error('Error submitting review:', error);
      throw error;
    }
  };

  const addToCart = async ()=>{
    const token = localStorage.getItem('token');
    if(!token){ return navigate('/login'); }
    await API.post('/cart/add',{productId:id, qty:1},{ headers:{ Authorization: 'Bearer '+token } });
    alert('Added to cart');
    navigate('/cart');
  };

  if (isLoading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-pink-500"></div>
      </div>
    );
  }

  if (!product || Object.keys(product).length === 0) {
    return (
      <div className="text-center p-8 text-gray-600">
        <p className="text-xl font-medium mb-2">Product not found</p>
        <button 
          onClick={() => navigate('/')}
          className="mt-4 px-4 py-2 bg-pink-500 text-white rounded hover:bg-pink-600 transition-colors"
        >
          Back to Home
        </button>
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
        {/* Product Image */}
        <div className="md:col-span-1">
          <div className="bg-white rounded-lg overflow-hidden shadow-md">
            <img 
              src={product.image} 
              alt={product.title} 
              className="w-full h-80 object-contain p-4"
            />
          </div>
        </div>

        {/* Product Details */}
        <div className="md:col-span-2">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">{product.title}</h1>

          {/* Rating and Reviews */}
          <div className="flex items-center mb-4">
            <div className="flex items-center">
              <StarRating rating={averageRating} size="md" />
              <span className="ml-2 text-sm text-gray-600">
                {averageRating.toFixed(1)} • {reviewCount} {reviewCount === 1 ? 'review' : 'reviews'}
              </span>
            </div>
            {token && !user?.isAdmin && (
              <button
                onClick={() => setShowReviewForm(!showReviewForm)}
                className="ml-4 text-sm text-pink-600 hover:text-pink-700"
              >
                {showReviewForm ? 'Cancel' : 'Write a review'}
              </button>
            )}
          </div>

          {/* Price */}
          <div className="text-3xl font-semibold text-gray-900 mb-6">
            {product?.price ? `₹${product.price.toFixed(2)}` : 'Price not available'}
            {product?.originalPrice && (
              <span className="ml-2 text-lg text-gray-500 line-through">
                ₹{product.originalPrice.toFixed(2)}
              </span>
            )}
            {product?.discount && (
              <span className="ml-2 text-sm font-medium text-white bg-green-500 rounded-full px-2 py-1">
                {product.discount}% OFF
              </span>
            )}
          </div>

          {/* Description */}
          <div className="prose max-w-none mb-6">
            <h3 className="text-lg font-medium text-gray-900 mb-2">Description</h3>
            <p className="text-gray-600">{product.description}</p>
          </div>

          {/* Add to Cart Button */}
          {!user?.isAdmin && (
            <button 
              onClick={addToCart} 
              className="bg-pink-600 text-white px-6 py-3 rounded-md hover:bg-pink-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-pink-500 transition-colors"
            >
              Add to cart
            </button>
          )}

          {/* Review Form */}
          {showReviewForm && (
            <div className="mt-8">
              <ReviewForm 
                onSubmit={handleReviewSubmit} 
                onCancel={() => setShowReviewForm(false)}
              />
            </div>
          )}
        </div>
      </div>

      {/* Reviews Section */}
      <div className="mt-16">
        <h2 className="text-2xl font-bold text-gray-900 mb-6">Customer Reviews</h2>

        {reviews.length > 0 ? (
          <div className="space-y-6">
            {reviews.map((review) => (
              <ReviewItem key={review._id} review={review} />
            ))}
          </div>
        ) : (
          <div className="text-center py-8">
            <p className="text-gray-500">No reviews yet. Be the first to review this product!</p>
            {token && !user?.isAdmin && !showReviewForm && (
              <button
                onClick={() => setShowReviewForm(true)}
                className="mt-4 text-pink-600 hover:text-pink-700 font-medium"
              >
                Write a review
              </button>
            )}
          </div>
        )}
      </div>
    </div>
  );
}
