import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { FaStar, FaStarHalfAlt, FaRegStar, FaCamera, FaCheck, FaTimes, FaChevronDown, FaChevronUp } from 'react-icons/fa';
import API from '../api';

const StarRating = ({ rating, onRatingChange, interactive = false }) => {
  return (
    <div className="flex">
      {[1, 2, 3, 4, 5].map((star) => {
        const starProps = {
          className: `w-5 h-5 ${interactive ? 'cursor-pointer' : 'cursor-default'}`,
          onClick: () => interactive && onRatingChange(star),
          onMouseEnter: () => interactive && onRatingChange(star)
        };

        return (
          <button
            key={star}
            type="button"
            className="focus:outline-none"
          >
            {rating >= star ? (
              <FaStar {...starProps} className={`${starProps.className} text-yellow-400`} />
            ) : rating >= star - 0.5 ? (
              <FaStarHalfAlt {...starProps} className={`${starProps.className} text-yellow-400`} />
            ) : (
              <FaRegStar {...starProps} className={`${starProps.className} text-gray-300`} />
            )}
          </button>
        );
      })}
    </div>
  );
};

const ReviewForm = ({ productId, onReviewSubmit }) => {
  const [rating, setRating] = useState(5);
  const [comment, setComment] = useState('');
  const [images, setImages] = useState([]);
  const [previews, setPreviews] = useState([]);

  const handleImageChange = (e) => {
    const files = Array.from(e.target.files);
    const newPreviews = files.map(file => URL.createObjectURL(file));
    setPreviews([...previews, ...newPreviews]);
    setImages([...images, ...files]);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('rating', rating);
    formData.append('comment', comment);
    images.forEach((image, index) => {
      formData.append(`images`, image);
    });
    onReviewSubmit(formData);
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4 p-4 border rounded-lg">
      <h3 className="text-lg font-semibold">Write a Review</h3>
      <div>
        <label className="block mb-2">Rating</label>
        <StarRating rating={rating} onRatingChange={setRating} interactive={true} />
      </div>
      <div>
        <label className="block mb-2">Review</label>
        <textarea
          value={comment}
          onChange={(e) => setComment(e.target.value)}
          className="w-full p-2 border rounded"
          rows="3"
          required
        />
      </div>
      <div>
        <label className="block mb-2">Add Photos (Optional)</label>
        <div className="flex items-center space-x-2">
          <label className="cursor-pointer bg-gray-100 hover:bg-gray-200 p-2 rounded">
            <Camera className="w-5 h-5" />
            <input
              type="file"
              multiple
              accept="image/*"
              onChange={handleImageChange}
              className="hidden"
            />
          </label>
          <span className="text-sm text-gray-500">{previews.length}/5 photos</span>
        </div>
        <div className="flex flex-wrap gap-2 mt-2">
          {previews.map((preview, index) => (
            <div key={index} className="relative">
              <img src={preview} alt="Preview" className="w-16 h-16 object-cover rounded" />
              <button
                type="button"
                onClick={() => {
                  const newPreviews = [...previews];
                  const newImages = [...images];
                  newPreviews.splice(index, 1);
                  newImages.splice(index, 1);
                  setPreviews(newPreviews);
                  setImages(newImages);
                }}
                className="absolute -top-2 -right-2 bg-red-500 text-white rounded-full w-5 h-5 flex items-center justify-center text-xs"
              >

              </button>
            </div>
          ))}
        </div>
      </div>
      <button
        type="submit"
        className="bg-pink-500 text-white px-4 py-2 rounded hover:bg-pink-600"
      >
        Submit Review
      </button>
    </form>
  );
};

const ReturnForm = ({ orderId, onReturnSubmit }) => {
  const [reason, setReason] = useState('');
  const [images, setImages] = useState([]);
  const [previews, setPreviews] = useState([]);

  const handleImageChange = (e) => {
    const files = Array.from(e.target.files);
    const newPreviews = files.map(file => URL.createObjectURL(file));
    setPreviews([...previews, ...newPreviews].slice(0, 5));
    setImages([...images, ...files].slice(0, 5));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('reason', reason);
    images.forEach(image => {
      formData.append('images', image);
    });
    onReturnSubmit(orderId, formData);
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4 p-4 border rounded-lg mt-4">
      <h3 className="text-lg font-semibold">Request a Return</h3>
      <div>
        <label className="block mb-2">Reason for Return</label>
        <select
          value={reason}
          onChange={(e) => setReason(e.target.value)}
          className="w-full p-2 border rounded"
          required
        >
          <option value="">Select a reason</option>
          <option value="Wrong Item">Wrong Item</option>
          <option value="Damaged Product">Damaged Product</option>
          <option value="Not as Described">Not as Described</option>
          <option value="Better Price Available">Better Price Available</option>
          <option value="Changed Mind">Changed Mind</option>
        </select>
      </div>
      <div>
        <label className="block mb-2">Upload Photos (Max 5)</label>
        <div className="flex items-center space-x-2">
          <label className="cursor-pointer bg-gray-100 hover:bg-gray-200 p-2 rounded">
            <Camera className="w-5 h-5" />
            <input
              type="file"
              multiple
              accept="image/*"
              onChange={handleImageChange}
              className="hidden"
              disabled={previews.length >= 5}
            />
          </label>
          <span className="text-sm text-gray-500">{previews.length}/5 photos</span>
        </div>
        <div className="flex flex-wrap gap-2 mt-2">
          {previews.map((preview, index) => (
            <div key={index} className="relative">
              <img src={preview} alt="Preview" className="w-16 h-16 object-cover rounded" />
              <button
                type="button"
                onClick={() => {
                  const newPreviews = [...previews];
                  const newImages = [...images];
                  newPreviews.splice(index, 1);
                  newImages.splice(index, 1);
                  setPreviews(newPreviews);
                  setImages(newImages);
                }}
                className="absolute -top-2 -right-2 bg-red-500 text-white rounded-full w-5 h-5 flex items-center justify-center text-xs"
              >

              </button>
            </div>
          ))}
        </div>
      </div>
      <button
        type="submit"
        className="bg-pink-500 text-white px-4 py-2 rounded hover:bg-pink-600"
      >
        Submit Return Request
      </button>
    </form>
  );
};

export default function ProductDetails() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [product, setProduct] = useState(null);
  const [activeTab, setActiveTab] = useState('description');
  const [showReviewForm, setShowReviewForm] = useState(false);
  const [showReturnForm, setShowReturnForm] = useState(false);
  const [userOrders, setUserOrders] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  const user = JSON.parse(localStorage.getItem('user'));
  const token = localStorage.getItem('token');

  useEffect(() => {
    const fetchData = async () => {
      try {
        setIsLoading(true);
        const [productRes, ordersRes] = await Promise.all([
          API.get(`/products/${id}`),
          token ? API.get('/orders/my-orders', {
            headers: { Authorization: `Bearer ${token}` }
          }) : { data: [] }
        ]);

        setProduct(productRes.data);
        setUserOrders(ordersRes.data || []);
      } catch (err) {
        setError(err.response?.data?.message || 'Error loading product details');
        console.error('Error:', err);
      } finally {
        setIsLoading(false);
      }
    };

    fetchData();
  }, [id, token]);

  const addToCart = async () => {
    if (!token) {
      navigate('/login');
      return;
    }
    try {
      await API.post(
        '/cart/add',
        { productId: id, qty: 1 },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      alert('Added to cart');
      navigate('/cart');
    } catch (err) {
      alert(err.response?.data?.message || 'Error adding to cart');
    }
  };

  const handleReviewSubmit = async (formData) => {
    try {
      await API.post(`/products/${id}/reviews`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
          Authorization: `Bearer ${token}`
        }
      });

      // Refresh product data
      const response = await API.get(`/products/${id}`);
      setProduct(response.data);
      setShowReviewForm(false);
      alert('Thank you for your review!');
    } catch (err) {
      alert(err.response?.data?.message || 'Error submitting review');
    }
  };

  const handleReturnSubmit = async (orderId, formData) => {
    try {
      await API.post(`/orders/${orderId}/return`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
          Authorization: `Bearer ${token}`
        }
      });

      setShowReturnForm(false);
      alert('Return request submitted successfully!');
    } catch (err) {
      alert(err.response?.data?.message || 'Error submitting return request');
    }
  };

  const hasPurchased = userOrders.some(order => 
    order.items.some(item => item.product._id === id) && 
    order.status === 'delivered'
  );

  const hasReviewed = user && product?.reviews?.some(review => 
    review.user?._id === user.id
  );

  if (isLoading) return <div className="text-center py-12">Loading product details...</div>;
  if (error) return <div className="text-center py-12 text-red-500">{error}</div>;
  if (!product) return <div className="text-center py-12">Product not found</div>;

  return (
    <div className="container mx-auto px-4 py-8">
      {/* Product Header */}
      <div className="bg-white rounded-lg shadow-md overflow-hidden">
        <div className="md:flex">
          {/* Product Image */}
          <div className="md:w-1/2 p-6">
            <div className="h-96 bg-gray-100 rounded-lg overflow-hidden flex items-center justify-center">
              <img 
                src={product.image} 
                alt={product.title} 
                className="max-h-full max-w-full object-contain"
              />
            </div>
          </div>

          {/* Product Info */}
          <div className="md:w-1/2 p-6">
            <h1 className="text-3xl font-bold text-gray-900 mb-2">{product.title}</h1>

            {/* Rating */}
            <div className="flex items-center mb-4">
              <div className="flex">
                <StarRating rating={product.rating || 0} />
              </div>
              <span className="ml-2 text-sm text-gray-600">
                ({product.numReviews || 0} reviews)
              </span>
            </div>

            {/* Price */}
            <div className="text-3xl font-bold text-pink-600 mb-6">
              {product.price.toFixed(2)}
            </div>

            {/* Stock Status */}
            <div className="mb-6">
              {product.stock > 0 ? (
                <span className="inline-flex items-center text-green-600">
                  <Check className="w-5 h-5 mr-1" />
                  In Stock ({product.stock} available)
                </span>
              ) : (
                <span className="inline-flex items-center text-red-600">
                  <X className="w-5 h-5 mr-1" />
                  Out of Stock
                </span>
              )}
            </div>

            {/* Add to Cart Button */}
            {!user?.isAdmin && (
              <button
                onClick={addToCart}
                className="w-full bg-pink-600 hover:bg-pink-700 text-white font-bold py-3 px-6 rounded-lg transition duration-200"
                disabled={product.stock === 0}
              >
                {product.stock > 0 ? 'Add to Cart' : 'Out of Stock'}
              </button>
            )}

            {/* Review/Return Buttons for logged-in users who purchased */}
            {user && hasPurchased && !user.isAdmin && (
              <div className="mt-4 space-y-2">
                {!hasReviewed && (
                  <button
                    onClick={() => setShowReviewForm(!showReviewForm)}
                    className="w-full bg-blue-100 hover:bg-blue-200 text-blue-800 font-medium py-2 px-4 rounded flex items-center justify-center"
                  >
                    {showReviewForm ? 'Cancel Review' : 'Write a Review'}
                    {showReviewForm ? (
                      <ChevronUp className="ml-2 w-5 h-5" />
                    ) : (
                      <ChevronDown className="ml-2 w-5 h-5" />
                    )}
                  </button>
                )}
                <button
                  onClick={() => setShowReturnForm(!showReturnForm)}
                  className="w-full bg-red-100 hover:bg-red-200 text-red-800 font-medium py-2 px-4 rounded flex items-center justify-center"
                >
                  {showReturnForm ? 'Cancel Return' : 'Request Return'}
                  {showReturnForm ? (
                    <ChevronUp className="ml-2 w-5 h-5" />
                  ) : (
                    <ChevronDown className="ml-2 w-5 h-5" />
                  )}
                </button>
              </div>
            )}

            {/* Product Meta */}
            <div className="mt-6 pt-6 border-t border-gray-200">
              <div className="grid grid-cols-2 gap-4 text-sm text-gray-600">
                <div>
                  <span className="font-medium">Category:</span> {product.category}
                </div>
                <div>
                  <span className="font-medium">SKU:</span> {product.sku || 'N/A'}
                </div>
                <div>
                  <span className="font-medium">Return Policy:</span> {product.returnPolicy}
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Review Form */}
        {showReviewForm && (
          <div className="px-6 pb-6">
            <ReviewForm productId={id} onReviewSubmit={handleReviewSubmit} />
          </div>
        )}

        {/* Return Form */}
        {showReturnForm && (
          <div className="px-6 pb-6">
            <ReturnForm 
              orderId={userOrders.find(order => 
                order.items.some(item => item.product._id === id) && 
                order.status === 'delivered'
              )?._id} 
              onReturnSubmit={handleReturnSubmit} 
            />
          </div>
        )}

        {/* Tabs */}
        <div className="border-t border-gray-200">
          <nav className="flex -mb-px">
            <button
              onClick={() => setActiveTab('description')}
              className={`py-4 px-6 text-center border-b-2 font-medium text-sm ${
                activeTab === 'description'
                  ? 'border-pink-500 text-pink-600'
                  : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
              }`}
            >
              Description
            </button>
            <button
              onClick={() => setActiveTab('reviews')}
              className={`py-4 px-6 text-center border-b-2 font-medium text-sm ${
                activeTab === 'reviews'
                  ? 'border-pink-500 text-pink-600'
                  : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
              }`}
            >
              Reviews ({product.reviews?.length || 0})
            </button>
          </nav>
        </div>

        {/* Tab Content */}
        <div className="p-6">
          {/* Description Tab */}
          {activeTab === 'description' && (
            <div>
              <h3 className="text-lg font-medium text-gray-900 mb-4">Product Description</h3>
              <p className="text-gray-600 whitespace-pre-line">{product.description}</p>

              {product.features && product.features.length > 0 && (
                <div className="mt-6">
                  <h4 className="text-md font-medium text-gray-900 mb-3">Features</h4>
                  <ul className="list-disc pl-5 space-y-2 text-gray-600">
                    {product.features.map((feature, index) => (
                      <li key={index}>{feature}</li>
                    ))}
                  </ul>
                </div>
              )}
            </div>
          )}

          {/* Reviews Tab */}
          {activeTab === 'reviews' && (
            <div>
              <div className="flex justify-between items-center mb-6">
                <h3 className="text-lg font-medium text-gray-900">Customer Reviews</h3>
                {user && hasPurchased && !hasReviewed && !showReviewForm && (
                  <button
                    onClick={() => setShowReviewForm(true)}
                    className="bg-pink-600 hover:bg-pink-700 text-white px-4 py-2 rounded-md text-sm font-medium"
                  >
                    Write a Review
                  </button>
                )}
              </div>

              {/* Review List */}
              <div className="space-y-6">
                {product.reviews && product.reviews.length > 0 ? (
                  product.reviews.map((review) => (
                    <div key={review._id} className="border-b border-gray-200 pb-6 last:border-0 last:pb-0">
                      <div className="flex items-center justify-between mb-2">
                        <div className="flex items-center">
                          <div className="w-10 h-10 rounded-full bg-gray-200 flex items-center justify-center text-gray-600 font-semibold">
                            {review.user?.name?.charAt(0) || 'U'}
                          </div>
                          <div className="ml-3">
                            <h4 className="font-medium text-gray-900">{review.user?.name || 'Anonymous'}</h4>
                            <div className="flex items-center">
                              <StarRating rating={review.rating} />
                              <span className="ml-2 text-sm text-gray-500">
                                {new Date(review.createdAt).toLocaleDateString()}
                              </span>
                            </div>
                          </div>
                        </div>
                        {review.isVerifiedPurchase && (
                          <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-green-100 text-green-800">
                            <Check className="w-3 h-3 mr-1" /> Verified Purchase
                          </span>
                        )}
                      </div>
                      <p className="text-gray-600 mt-2">{review.comment}</p>

                      {/* Review Images */}
                      {review.images && review.images.length > 0 && (
                        <div className="flex flex-wrap gap-2 mt-3">
                          {review.images.map((img, idx) => (
                            <img
                              key={idx}
                              src={img}
                              alt={`Review ${idx + 1}`}
                              className="w-20 h-20 object-cover rounded-md border border-gray-200"
                              onClick={() => {
                                // Show full screen image viewer
                                window.open(img, '_blank');
                              }}
                              style={{ cursor: 'pointer' }}
                            />
                          ))}
                        </div>
                      )}
                    </div>
                  ))
                ) : (
                  <div className="text-center py-8">
                    <p className="text-gray-500">No reviews yet. Be the first to review!</p>
                    {user && hasPurchased && !hasReviewed && !showReviewForm && (
                      <button
                        onClick={() => setShowReviewForm(true)}
                        className="mt-4 bg-pink-600 hover:bg-pink-700 text-white px-4 py-2 rounded-md text-sm font-medium"
                      >
                        Write a Review
                      </button>
                    )}
                  </div>
                )}
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
